css:
body {
    background-color: #f8f9fa;
    font-family: 'Arial', sans-serif;
}

/* Style the main container */
.container {
    max-width: 800px;
    margin: 0 auto;
    background-color: #fff;
    padding: 20px;
    border-radius: 10px;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
}

/* Style the header section */
.header {
    text-align: center;
    margin-bottom: 20px;
}

/* Style the company name */
.company-name {
    font-size: 24px;
    color: #333;
    font-weight: bold;
}

/* Style the company address */
.company-address {
    font-size: 16px;
    color: #666;
}

/* Style the invoice details */
.invoice-details {
    font-size: 16px;
    color: #666;
    margin-bottom: 10px;
}

/* Style the table */
table {
    width: 100%;
    border-collapse: collapse;
    margin-bottom: 20px;
}

/* Style the table header */
th {
    background-color: #f2f2f2;
    padding: 10px;
    text-align: left;
    font-weight: normal;
    color: #333;
    border-bottom: 2px solid #ddd;
}

/* Style the table rows */
td {
    padding: 10px;
    border-bottom: 1px solid #ddd;
}

/* Style the footer section */
.footer {
    margin-top: 20px;
    text-align: right;
}

/* Style the total amount */
.total-amount {
    font-size: 18px;
    color: #333;
    font-weight: bold;
}

/* Style the footer */
.footer-text {
    font-size: 12px;
    color: #999;
    margin-top: 10px;
}

/* Style the signature */
.signature {
    text-align: right;
    margin-top: 50px;
}

/* Style the declaration */
.declaration {
    text-align: left;
    margin-top: 30px;
    font-style: italic;
}
